var $userinfo = _readCSVFile($path, { includeHeader: true });
var $headers = $userinfo[0];


var $firstNameIndex = $headers.indexOf($headers[0]);
var $lastNameIndex = $headers.indexOf($headers[1]);
var $phoneIndex = $headers.indexOf($headers[2]);
var $addressIndex = $headers.indexOf($headers[3]);
var $emailIndex = $headers.indexOf($headers[4]);


var $firstNameUI = _collectAttributes("_cell", "/.*/", "sahiText", _under(_tableHeader("First Name"))).slice(0, $userinfo.length - 1).reverse();
var $lastNameUI = _collectAttributes("_cell", "/.*/", "sahiText", _under(_tableHeader("Last Name"))).slice(0, $userinfo.length - 1).reverse();
var $phoneUI = _collectAttributes("_cell", "/.*/", "sahiText", _under(_tableHeader("Mobile Number"))).slice(0, $userinfo.length - 1).reverse();
var $addressUI = _collectAttributes("_cell", "/.*/", "sahiText", _under(_tableHeader("Address"))).slice(0, $userinfo.length - 1).reverse();
var $emailUI = _collectAttributes("_cell", "/.*/", "sahiText", _under(_tableHeader("Email"))).slice(0, $userinfo.length - 1).reverse();


var $userinfo1 = _readCSVFile($path, { includeHeader: false });


var $filteredUserInfo = $userinfo1.filter(row => row[$phoneIndex].trim() !== "");


var $filteredLength = $filteredUserInfo.length;

_log($filteredLength)


for (var $i = 0; $i < $filteredLength; $i++) {
    _assertEqual($filteredUserInfo[$i][$firstNameIndex], $firstNameUI[$i]);
    _assertEqual($filteredUserInfo[$i][$lastNameIndex], $lastNameUI[$i]);
    _assertEqual($filteredUserInfo[$i][$phoneIndex], $phoneUI[$i]);
    _assertEqual($filteredUserInfo[$i][$addressIndex], $addressUI[$i]);
    _assertEqual($filteredUserInfo[$i][$emailIndex], $emailUI[$i]);
}
