var $currentDate = new Date();

var $istOffset = 5.5 * 60 * 60 * 1000; // IST offset in milliseconds
var $currentDateInIST = new Date($currentDate.getTime() + $istOffset);

var $day = String($currentDateInIST.getUTCDate()).padStart(2, '0');
var $monthNames = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
var $month = $monthNames[$currentDateInIST.getUTCMonth()];
var $year = $currentDateInIST.getUTCFullYear();

// 24-hour format
var $hours = String($currentDateInIST.getUTCHours()).padStart(2, '0');
var $minutes = String($currentDateInIST.getUTCMinutes()).padStart(2, '0');
var $seconds = String($currentDateInIST.getUTCSeconds()).padStart(2, '0');
var $ampm = $hours >= 12 ? 'PM' : 'AM';

var $newFormattedDate = `${$day} ${$month} ${$year} ${$hours}:${$minutes}:`;

$newFormattedDate;

