steps.rawScript:
var $table = _table("table-default table-hover");

// Locate the row containing "USA" in the "Country" column
var $usaRow = _rightOf(_cell(_tableHeader("Country"), "USA"));

var $isMessagingGuidelinesPresent = _contains($usaRow, _paragraph("/Messaging Guidelines/"));

_assert($isMessagingGuidelinesPresent, "Messaging Guidelines is present in the USA row");

$table = _table("table-default table-hover");

$usaRow = _rightOf(_cell(_tableHeader("Country"), "USA"));

_wait(2000);

_click(_paragraph("/Messaging Guidelines/", $usaRow));

_wait(2000);

var $isUSARestrictionsPresent = _isVisible(_heading4("mb-4 mt-0", "USA Restrictions And Best Practices"));

var $isCountryRestrictionsPresent = _isVisible(_heading4("mt-3 mb-4", "Country Restrictions"));

_assert($isUSARestrictionsPresent, "USA Restrictions And Best Practices element is present");
_assert($isCountryRestrictionsPresent, "Country Restrictions element is present");
