steps.rawScript:
var $cards = _collectAttributes("_div","/rounded/","sahiText",_under(_heading4("Setup your account")),_in(_div("slick-list")));
while(_isVisible(_image("slick-arrow slick-next"))){
_click(_image("slick-arrow slick-next"));
var $cardsNext = _collectAttributes("_div","/rounded/","sahiText",_under(_heading4("Setup your account")),_in(_div("slick-list")));
for(var $i=0;$i<$cardsNext.length;$i++){
for(var $j=0;$j<$cards.length;$j++){
if($cards[$j]!=$cardsNext[$i]){
$cards.push($cardsNext[$i]);
}
}
}
}
_log($cards.length)
for(var $i=0; $i<$cards.length-1; $i++){
//_mouseOver(_svg_path(0,_under(_heading3("/Welcome to Conversive/"))));
_click(_svg_path(0,_under(_heading3("/Welcome to Conversive/"))));
_click(_submit("Skip This Step"));
var $frstCard = _getText(_div("/text-center/",_under(_heading3("/Welcome to Conversive/"))));
_assertEqual($frstCard,$cards[$i])
}
_assertNotVisible(_heading4("Setup your account"))

while(_isVisible(_heading4("Setup your account"))){
var $cards = _collectAttributes("_div","/rounded/","sahiText",_under(_heading4("Setup your account")),_in(_div("slick-list")));

for(var $i=0; $i<$cards.length; $i++){
//_mouseOver(_svg_path(0,_under(_heading3("/Welcome to Conversive/"))));
_click(_svg_path(0,_under(_heading3("/Welcome to Conversive/"))));
_click(_submit("Skip This Step"));
_wait(2000)
var $frstCard = _getText(_div("/text-center/",_under(_heading3("/Welcome to Conversive/"))));
_assertEqual($frstCard,$cards[$i])
}
}
