steps.rawScript:
_setStrictVisibilityCheck(false);
var $expand = _collect("_span","/text-lg mt/",_in(_div("side-nav-content")));
var $dropdowns = []
for(var $i=0; $i<$expand.length; $i++){
_click($expand[$i]);
var $item = _getText(_parentNode($expand[$i],"DIV"));
_log($item)
_call(_div($item).scrollIntoView())
var $a = _collectAttributes("_link","/.*/","sahiText",_in(_list("/ml-5/"+"["+$i+"]")));
var $link = _getText(_link(0,_in(_list("/ml-5/"+"["+$i+"]"))));
for(var $j=0; $j<$a.length; $j++){
$dropdowns.push($a[$j]);
_assertVisible(_div($a[$j],_under(_div($item))));
if($j<$a.length-1){
_assertVisible(_div($a[$j+1],_under(_div($a[$j]))));
}
}
}

var $menuItems = _collectAttributes("_div","/item-transparent/","sahiText",_in(_div("side-nav-content")));
for (var $i = 0; $i < $menuItems.length; $i++) {
    for (var $j = 0; $j < $dropdowns.length; $j++) {
        if ($menuItems[$i] === $dropdowns[$j]) {
            $menuItems.splice($i, 1); // Remove the matching element from $menu
            $i--; // Adjust the index after removal
            break; // Exit the inner loop after removing the element
        }
    }
}

for(var $k=0; $k<$menuItems.length-1; $k++){
//_log($menuItems[$k]);
_assertVisible(_div($menuItems[$k+1],_under(_div($menuItems[$k]))));
}
